    class TrieNode{
        TrieNode[] child;
        TrieNode(){
            child = new TrieNode[26];
        }
    }

    TrieNode root = new TrieNode();
    void insertInTrie(String str){
        TrieNode temp = root;
        for(int i=0; i<str.length(); i++){
            int currChar = str.charAt(i) - 'a';
            if(temp.child[currChar]==null) temp.child[currChar] = new TrieNode();
            temp = temp.child[currChar];
        }
    }

    public String solve(String[] A, String[] B) {
        //lets insert strings in tries
        for(int i=0; i<A.length; i++){
            String str = A[i];
            insertInTrie(str);
        }

        StringBuilder ans = new StringBuilder();
        //lets find now if string from b is possible or not
        for(int i=0; i<B.length; i++){
            ans.append(findIfPossible(B[i]));
        }

        return ans.toString();
    }

    int findIfPossible(String str){
        TrieNode temp = root;
        for(int i=0; i<str.length(); i++){
            int currChar = str.charAt(i) - 'a';
            if(temp.child[currChar]==null){
                //making sure we are not on last element
                if(i==str.length()-1){
                    return 1;
                }
                String subString = str.substring(i+1);
                //we have got till prefix lets look for suffix
                //lets replace the char with every possible thing then see how it goes
                for(int j=0; j<26; j++){
                    String newStr = ((char) ('a'+j))+subString;
                    if(ifPossible(newStr, temp) ) return 1;
                }
                break;
            }
            temp = temp.child[currChar];
        }
        return 0;
    }

    boolean ifPossible(String str, TrieNode root){
        if(root==null) return false;
        TrieNode temp = root;
        for(int i=0; i<str.length(); i++){
            int currChar = str.charAt(i)-'a';
            if(temp.child[currChar]==null) return false;
            temp = temp.child[currChar];
        }
        return true;
    }
